import {
  GET_ALL__SERVICES_SUCCESS,
  GET__SERVICE_SUCCESS,
  GET_ALL__SERVICES_ERROR,
  GET__SERVICE_ERROR,
} from "../actions/type";

const initialState = {
  Services: [],
  Service: null,
  Loading: true,
  error: null,
};

// eslint-disable-next-line import/no-anonymous-default-export
export default function (state = initialState, action) {
  const { payload, type } = action;

  switch (type) {
    case GET_ALL__SERVICES_SUCCESS:
      return {
        Services: payload,
        Loading: false,
      };
    case GET__SERVICE_SUCCESS:
      return {
        Service: payload,
        Loading: false,
      };
    case GET_ALL__SERVICES_ERROR:
    case GET__SERVICE_ERROR:
      return {
        Loading: false,
        Services: [],
        Service: null,
        error: payload,
      };
    default:
      return state;
  }
}
