import { SUCCESS_COUPON, FAILED_COUPON } from "../actions/type";

const initialState = {
  coupon: null,
  validate: false,
  error: false,
  couponLoading: true,
};

export default function (state = initialState, action) {
  const { payload, type } = action;

  switch (type) {
    case SUCCESS_COUPON:
      return {
        coupon: payload,
        validate: true,
        couponLoading: false,
      };
    case FAILED_COUPON:
      return {
        validate: false,
        error: true,
        couponLoading: false,
      };
    default:
      return state;
  }
}
